export function FavoriteToggle({
  note,
  onFavoriteToggle,
}: {
  note: Note;  // Expecting the entire Note object
  onFavoriteToggle: (noteId: number, isFavorited: boolean) => void;
}) {
  const [favorite, setFavorite] = useState(false);

  const handleClick = () => {
    setFavorite(!favorite);
    onFavoriteToggle(note.id, !favorite);  // Use note.id to toggle favorite status
  };

  return (
    <button
      onClick={handleClick}
      style={{
        background: 'none',
        border: 'none',
        cursor: 'pointer',
      }}
    >
      {favorite ? '❤️' : '♡'}
    </button>
  );
}
