import {Label, Note} from "./types"
import { dummyNotesList } from "./constants"
import { ClickCounter } from "./starterFiles/hooksExercise"
import { FavoriteToggle } from './starterFiles/favoriteExercise';
import './App.css';

function App(){
  const updateFavorites = (noteId, newFavoriteState) => {
    const updatedNotes = notes.map((note) => {
      if (note.id === noteId) {
        return { ...note, isFavorite: newFavoriteState };
      }
      return note;
    });
    setNotes(updatedNotes); // Update the state with the modified notes array
  };
  return (
    <div className = 'app-container'>
      <form className = "note-form">
        <div><input placeholder = "Note Title"></input></div>
        <div><textarea></textarea></div>
        <div><button type = "submit">Create Note</button></div>
      </form>
      <div className="notes-grid">
       {dummyNotesList.map((note) => (
         <div
           key={note.id}
           className="note-item">
           <div className="notes-header">
             <button>x</button>
           </div>
           <h2> {note.title} </h2>
           <p> {note.content} </p>
           <p> {note.label} </p>
           <FavoriteToggle
              isFavoriteInitially={note.isFavorite}
              onFavoriteChange={(newFavoriteState) => updateFavorites(note.id, newFavoriteState)}
            />
         </div>
       ))}
     </div>
     <ClickCounter></ClickCounter>
    </div>
  );
}


export default App;
