import { useState } from 'react'
import { ThemeContext, themes } from "./themeContext";

export function ToggleTheme() {
    const [currTheme, setCurrTheme] = useState(themes.light);

    const toggleTheme = () => {
        setCurrTheme(currTheme === themes.light ? themes.dark : themes.light);
    };
    const theme = useContext(ThemeContext);
    return (
        <ThemeContext.Provider value={currTheme}>
            <button 
                onClick={toggleTheme} 
                style={{
                    background: currTheme.foreground, 
                    color: currTheme.background, 
                    padding: "none",
                    border: "none", 
                    cursor: "pointer"
                }}
            >
                {currTheme === themes.light ? '☀︎' : '☼'}
            </button>
        </ThemeContext.Provider>
    );

}

export default ToggleTheme;
