import React, { useState } from 'react';

export function FavoriteToggle({ note, onFavoriteToggle }) {
    const [favorite, setFavorite] = useState(false);

    const handleClick = () => {
        setFavorite(!favorite);
        onFavoriteToggle(note, !favorite);  // Pass note and its favorite status to parent
    };

    return (
        <button onClick={handleClick} style={{
                background: 'none', 
                border: 'none', 
                cursor: 'pointer'
            }}>
            {favorite ? '❤️' : '♡'}
        </button>
    );
}
