import { useState } from 'react';
import { dummyNotesList } from './constants';
import { FavoriteToggle } from './starterFiles/favoriteExercise';
import './App.css';

function App() {
    const [favorites, setFavorites] = useState<string[]>([]);

    const updateFavorites = (note: string, isFavorite: boolean) => {
        setFavorites((prevFavorites) => {
            if (isFavorite) {
                return [...prevFavorites, note];
            } else {
                return prevFavorites.filter((favNote) => favNote !== note);
            }
        });
    };

    return (
        <div className='app-container'>
            <form className='note-form'>
                <div><input placeholder='Note Title'></input></div>
                <div><textarea></textarea></div>
                <div><button type='submit'>Create Note</button></div>
            </form>

            <div className="favoriteList">
                <h3>Favorite Titles</h3>
                {favorites.length === 0 ? <p>No favorites yet</p> : (
                    <ul>
                        {favorites.map((favorite, index) => (
                            <li key={index}>{favorite}</li>
                        ))}
                    </ul>
                )}
            </div>

            <div className="notes-grid">
                {dummyNotesList.map((note) => (
                    <div key={note.id} className="note-item">
                        <div className="notes-header">
                            <button>x</button>
                        </div>
                        <h2>{note.title}</h2>
                        <p>{note.content}</p>
                        <p>{note.label}</p>
                        <FavoriteToggle note={note.title} updateFavorites={updateFavorites} />
                    </div>
                ))}
            </div>
        </div>
    );
}

export default App;
