import {Label, Note} from "./types";
import { dummyNotesList } from "./constants";
import { FavoriteToggle } from './starterFiles/favoriteExercise';
import './App.css';
import React, { useState } from 'react';

function App() {
  const [favoritedNotes, setFavoritedNotes] = useState([]);

  // Function to handle when a note is favorited/unfavorited
  const handleFavoriteToggle = (note, isFavorited) => {
    setFavoritedNotes(prevFavorites => {
      if (isFavorited) {
        return [...prevFavorites, note];  // Add to favorites
      } else {
        return prevFavorites.filter(favNote => favNote.id !== note.id);  // Remove from favorites
      }
    });
  };

  return (
    <div className='app-container'>
      <form className="note-form">
        <div><input placeholder="Note Title"></input></div>
        <div><textarea></textarea></div>
        <div><button type="submit">Create Note</button></div>
      </form>

      {/* Favorite Notes List */}
      <div className="favoriteList">
        {favoritedNotes.map((favorite, index) => (
          <p key={index}>{favorite.title}</p>
        ))}
      </div>

      {/* Notes Grid */}
      <div className="notes-grid">
        {dummyNotesList.map((note) => (
          <div key={note.id} className="note-item">
            <div className="notes-header">
              <button>x</button>
            </div>
            <h2>{note.title}</h2>
            <p>{note.content}</p>
            <p>{note.label}</p>
            <FavoriteToggle note={note} onFavoriteToggle={handleFavoriteToggle}></FavoriteToggle>
          </div>
        ))}
      </div>
    </div>
  );
}

export default App;
